    color: ['clair', 'transparent', 'opaque', 'coloré'][random(0, 3)],
    texture: ['lisse', 'fin', 'rigide'][random(0, 2)],
    weight: random(1, 4) / 10
  };
}

// Fonction pour identifier le type de produit
function identifyProductType(features) {
  let bestMatch = null;
  let bestScore = 0;

  for (const [productId, productFeatures] of Object.entries(visualFeatures)) {
    let score = 0;

    // Comparer chaque caractéristique
    for (const [key, value] of Object.entries(features)) {
      if (key === 'shape' && productFeatures.shape === value) {
        score += 2;
      } else if (key === 'transparency' && 
                 Math.abs(productFeatures.transparency - value) < 0.2) {
        score += 1;
      } else if (key === 'shine' && 
                 Math.abs(productFeatures.shine - value) < 0.2) {
        score += 1;
      } else if (key === 'color' && 
                 productFeatures.color.includes(value)) {
        score += 1;
      } else if (key === 'texture' && 
                 productFeatures.texture === value) {
        score += 1;
      } else if (key === 'weight' && 
                 Math.abs(productFeatures.weight - value) < 0.1) {
        score += 1;
      }
    }

    if (score > bestScore) {
      bestScore = score;
      bestMatch = productId;
    }
  }

  return {
    productId: bestMatch,
    confidence: Math.min(100, Math.round((bestScore / 7) * 100))
  };
}

// Fonction principale d'analyse d'image
async function analyzeImage(imageData) {
  console.log('Analyse de l\'image en cours...');

  try {
    // Détecter les caractéristiques visuelles
    const features = detectVisualFeatures(imageData);
    console.log('Caractéristiques détectées:', features);

    // Identifier le type de produit
    const identification = identifyProductType(features);
    console.log('Produit identifié:', identification);

    // Vérifier si le produit existe dans la base de données
    if (!products[identification.productId]) {
      console.warn('Produit non trouvé, utilisation du produit par défaut');
      identification.productId = 'plastic-container';
    }

    // Calculer le score environnemental
    const product = products[identification.productId];
    const score = calculateEnvironmentalScore(product);
    console.log('Score environnemental calculé:', score);

    return {
      productId: identification.productId,
      confidence: identification.confidence,
      features: features,
      score: score
    };
  } catch (error) {
    console.error('Erreur lors de l\'analyse:', error);
    // Retourner un produit par défaut en cas d'erreur
    return {
      productId: 'plastic-container',
      confidence: 50,
      features: {
        shape: 'rectangulaire',
        transparency: 0.5,
        shine: 0.6,
        color: 'clair',
        texture: 'rigide',
        weight: 0.4
      },
      score: 3
    };
  }
}

