import { createRouter, createWebHistory } from 'vue-router'
import Layout from './Layout.vue'

const routes = [
  {
    path: '/',
    component: Layout,
    children: [
      {
        path: '',
        name: 'Home',
        component: () => import('./HomePage.vue') // ton ancien App.vue renommé
      },
      {
        path: 'infos',
        name: 'Infos',
        component: () => import('./InfoPage.vue')
      }
    ]
  }
]
